    ) VALUES (
        p_instance_id, v_current_stage, p_to_stage, p_action,
        p_user_id, p_remarks, p_data_json
    );
    
    
    UPDATE workflow_instances 
    SET current_stage = p_to_stage,
        completed_at = CASE 
            WHEN p_to_stage IN ('completed', 'cancelled') THEN CURRENT_TIMESTAMP
            ELSE NULL
        END
