DROP PROCEDURE IF EXISTS `sp_deny_permission`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_deny_permission` (IN `p_user_id` INT UNSIGNED, IN `p_permission_code` VARCHAR(255), IN `p_reason` VARCHAR(255), IN `p_changed_by` INT UNSIGNED, OUT `p_success` TINYINT)  MODIFIES SQL DATA BEGIN
    DECLARE perm_id INT;
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN
        SET p_success = 0;
    END;
    
    
    SELECT id INTO perm_id FROM permissions WHERE code = p_permission_code LIMIT 1;
    
    IF perm_id IS NULL THEN
        SET p_success = 0;
    ELSE
        INSERT INTO user_permissions (user_id, permission_id, permission_type, reason, granted_by)
        VALUES (p_user_id, perm_id, 'deny', p_reason, p_changed_by)
        ON DUPLICATE KEY UPDATE 
            permission_type = 'deny',
            reason = p_reason,
            updated_at = NOW();
        
        
        IF EXISTS(SELECT 1 FROM INFORMATION_SCHEMA.TABLES 
            WHERE TABLE_SCHEMA = 'KingsWayAcademy' AND TABLE_NAME = 'permission_audit_log') THEN
            INSERT INTO permission_audit_log (action, user_id, permission_id, changed_by, reason)
            VALUES ('deny_permission', p_user_id, perm_id, p_changed_by, p_reason);
        END IF;
        
        SET p_success = 1;
    END IF;
END$$

