CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_get_user_permissions` (IN `p_user_id` INT UNSIGNED)  READS SQL DATA BEGIN
    
    SELECT DISTINCT 
        p.id,
        p.code as permission_code,
        p.entity,
        p.action,
        p.description,
        'role' as source,
        r.name as role_name
    FROM user_roles ur
    JOIN roles r ON ur.role_id = r.id
    JOIN role_permissions rp ON r.id = rp.role_id
    JOIN permissions p ON rp.permission_id = p.id
    WHERE ur.user_id = p_user_id
    
