DROP PROCEDURE IF EXISTS `sp_grant_permission`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_grant_permission` (IN `p_user_id` INT UNSIGNED, IN `p_permission_code` VARCHAR(255), IN `p_reason` VARCHAR(255), IN `p_granted_by` INT UNSIGNED, IN `p_expires_at` TIMESTAMP, OUT `p_success` TINYINT)  MODIFIES SQL DATA BEGIN
    DECLARE perm_id INT;
    DECLARE EXIT HANDLER FOR SQLEXCEPTION 
    BEGIN
        SET p_success = 0;
    END;
    
    
    SELECT id INTO perm_id FROM permissions WHERE code = p_permission_code LIMIT 1;
    
    IF perm_id IS NULL THEN
        SET p_success = 0;
    ELSE
        INSERT INTO user_permissions (user_id, permission_id, permission_type, reason, granted_by, expires_at)
        VALUES (p_user_id, perm_id, 'grant', p_reason, p_granted_by, p_expires_at)
        ON DUPLICATE KEY UPDATE 
            permission_type = 'grant',
            reason = p_reason,
            granted_by = p_granted_by,
            expires_at = p_expires_at,
            updated_at = NOW();
        
        
        IF EXISTS(SELECT 1 FROM INFORMATION_SCHEMA.TABLES 
            WHERE TABLE_SCHEMA = 'KingsWayAcademy' AND TABLE_NAME = 'permission_audit_log') THEN
            INSERT INTO permission_audit_log (action, user_id, permission_id, changed_by, reason)
            VALUES ('grant_permission', p_user_id, perm_id, p_granted_by, p_reason);
        END IF;
        
        SET p_success = 1;
    END IF;
END$$

DROP PROCEDURE IF EXISTS `sp_implement_discipline_action`$$
