CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_users_with_multiple_roles` ()  READS SQL DATA COMMENT 'Returns users with more than one role' BEGIN
    SELECT 
        u.id as user_id,
        CONCAT(u.first_name, ' ', u.last_name) as user_name,
        u.email,
        COUNT(ur.role_id) as role_count,
        GROUP_CONCAT(r.name) as roles
    FROM users u
    JOIN user_roles ur ON u.id = ur.user_id
    JOIN roles r ON ur.role_id = r.id
    GROUP BY u.id, u.first_name, u.last_name, u.email
    HAVING COUNT(ur.role_id) > 1
    ORDER BY role_count DESC, u.first_name, u.last_name;
END$$

DROP PROCEDURE IF EXISTS `sp_users_with_permission`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_users_with_permission` (IN `p_permission_code` VARCHAR(255))  READS SQL DATA COMMENT 'Returns all users who have a specific permission (role-based or direct)' BEGIN
    DECLARE perm_id INT;
    
    SELECT id INTO perm_id FROM permissions WHERE code = p_permission_code LIMIT 1;
    
    IF perm_id IS NULL THEN
        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Permission not found';
    END IF;
    
    SELECT DISTINCT 
        u.id as user_id,
        CONCAT(u.first_name, ' ', u.last_name) as user_name,
        u.email,
        'role' as source,
        r.name as role_name
    FROM users u
    JOIN user_roles ur ON u.id = ur.user_id
    JOIN role_permissions rp ON ur.role_id = rp.role_id
    WHERE rp.permission_id = perm_id
    
    UNION DISTINCT
    
    SELECT 
        u.id,
        CONCAT(u.first_name, ' ', u.last_name),
        u.email,
        up.permission_type,
        NULL
    FROM users u
    JOIN user_permissions up ON u.id = up.user_id
    WHERE up.permission_id = perm_id
        AND up.permission_type IN ('grant', 'override')
        AND (up.expires_at IS NULL OR up.expires_at > NOW())
    
    ORDER BY user_name;
END$$

DROP PROCEDURE IF EXISTS `sp_users_with_role`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_users_with_role` (IN `p_role_name` VARCHAR(50))  READS SQL DATA COMMENT 'Returns all users assigned to a specific role' BEGIN
    DECLARE role_id INT UNSIGNED;
    
    SELECT id INTO role_id FROM roles WHERE name = p_role_name LIMIT 1;
    
    IF role_id IS NULL THEN
        SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Role not found';
    END IF;
    
    SELECT 
        u.id as user_id,
        CONCAT(u.first_name, ' ', u.last_name) as user_name,
        u.email,
        ur.created_at as assigned_at
    FROM users u
    JOIN user_roles ur ON u.id = ur.user_id
    WHERE ur.role_id = role_id
