DROP PROCEDURE IF EXISTS `sp_user_get_permission_summary`$$
CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_user_get_permission_summary` (IN `p_user_id` INT UNSIGNED)  READS SQL DATA COMMENT 'Returns summary: effective permissions, direct grants, denials, expiring soon' BEGIN
    SELECT 
        'total_permissions' as metric,
        COUNT(DISTINCT CASE WHEN source = 'role' THEN permission_id END) as role_based,
        COUNT(DISTINCT CASE WHEN source = 'grant' THEN permission_id END) as direct_grants,
        COUNT(DISTINCT CASE WHEN source = 'override' THEN permission_id END) as overrides
    FROM (
        SELECT DISTINCT p.id as permission_id, 'role' as source
        FROM user_roles ur
        JOIN role_permissions rp ON ur.role_id = rp.role_id
        JOIN permissions p ON rp.permission_id = p.id
        WHERE ur.user_id = p_user_id
        
        UNION ALL
        
        SELECT p.id, up.permission_type
        FROM user_permissions up
        JOIN permissions p ON up.permission_id = p.id
        WHERE up.user_id = p_user_id
            AND up.permission_type IN ('grant', 'override')
            AND (up.expires_at IS NULL OR up.expires_at > NOW())
    ) perm_union;
    
    SELECT 
        'denied_count' as metric,
        COUNT(*) as count
    FROM user_permissions up
    WHERE up.user_id = p_user_id 
        AND up.permission_type = 'deny'
        AND (up.expires_at IS NULL OR up.expires_at > NOW());
    
    SELECT 
        'expiring_soon' as metric,
        COUNT(*) as count
    FROM user_permissions up
    WHERE up.user_id = p_user_id 
        AND up.permission_type IN ('grant', 'override')
        AND up.expires_at IS NOT NULL
        AND up.expires_at > NOW()
        AND up.expires_at <= DATE_ADD(NOW(), INTERVAL 7 DAY);
END$$

