    IF NEW.status IN ('pending', 'partial')
    AND NEW.due_date < CURDATE() THEN
        SELECT COUNT(*) INTO v_arrears_exists
        FROM student_arrears
        WHERE student_id = NEW.student_id
          AND academic_year = NEW.academic_year
